mapper.update:
update game_server
set game_id = #{gameId,jdbcType=INTEGER},
  server_name = #{serverName,jdbcType=VARCHAR},
  open_server_time = #{openServerTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, game_id, server_name, open_server_time
from game_server
where id = #{id,jdbcType=INTEGER}
